using System.ComponentModel.DataAnnotations;namespace PersonalInfoSampleApp.Model{    public class Address    {        [Key]        public int Id { get; set; }        [StringLength(50)]        [Required]        public string Street { get; set; }        [StringLength(20)]        [Required]        public string ResidenceNumber { get; set; }        [StringLength(6)]        [Required]        public string PostalNumber { get; set; }        [Required]        public int CityId { get; set; }        public City City { get; set; }    }}