using System;using System.ComponentModel.DataAnnotations;namespace PersonalInfoSampleApp.Model{    public class PersonalInfo    {        [Key]        public int Id { get; set; }        [StringLength(50)]        [Required]        public string FirstName { get; set; }        [StringLength(50)]        [Required]        public string LastName { get; set; }        [DataType(DataType.Date)]        [Required]        public DateTime DateOfBirth { get; set; }        public bool UseSameAddress { get; set; }        [Required]        public Address ResidenceAddress { get; set; }        public int ResidenceAddressId { get; set; }        public Address CorrespondenceAddress { get; set; }    }}